changeSet.sql:
Create table if not exists product ( id bigserial PRIMARY KEY, name varchar NOT NULL, price float NOT NULL );
Create table if not exists company ( id bigserial PRIMARY KEY, name varchar NOT NULL );
Create table if not exists company_product ( product_id bigint NOT NULL, company_id bigint NOT NULL, FOREIGN KEY (product_id) REFERENCES product (id), FOREIGN KEY (company_id) REFERENCES company (id) );
Create table if not exists users ( id bigserial PRIMARY KEY, name varchar NOT NULL, surname varchar, email varchar NOT NULL UNIQUE, role varchar NOT NULL, enabled boolean NOT NULL DEFAULT FALSE, password varchar(255) NOT NULL );
